function showList($array, $listType = 'ol') {
    $ret = '<'.$listType.'>'.PHP_EOL;
    foreach($array as $key => $value) {
        if(is_array($value)) {
            $ret .= '<li><strong>'.$key.'</strong>'.PHP_EOL.showList($value).'</li>'.PHP_EOL;
        } else {
            $ret .= '<li>'.$value.'</li>'.PHP_EOL;
        }
    }
    $ret .= '</'.$listType.'>'.PHP_EOL;
    return $ret;
}


